import { proto } from '@adiwajshing/baileys'
import { ICommand, IMessage } from '../Types'

export let handler: ICommand = async (m: IMessage, { args }): Promise<void | proto.WebMessageInfo | any> => {
  if (!m.quoted?.key?.fromMe) return m.reply('bot tidak bisa menghapus pesan tersebut. silahkan reply pesan dari bot.')
  m.quoted.delete()
}
handler.tags = ['bot']
handler.help = ['delete'].map((x) => `${x} <reply>`)
handler.tags = ['bot']
handler.command = /^(delete|del)$/i
