import { ICommand, IMessage } from '../Types'

export let handler: ICommand = async (m: IMessage, { args }): Promise<any> => {
  if (!args[0]) return m.reply('Please Input a Emoji')
  let emoji = args[0]
  if (!m.quoted) return m.reply('You need to quote a message to use this command.')
  m.quoted.react(emoji)
}
handler.tags = ['fun']
handler.help = ['react'].map((v) => v + ' <emoji>')
handler.command = /^(react|reaction)$/i
